  useEffect(() => {
    fetch("/donate/3")
      .then((res) => {
        if (res.ok) {
          return res.json();
        }
      })
      .then((jsonRes) => setNotes(jsonRes));
  });

  return (
    <div>
      <div className="donate-content-container">
