import { buildQueries } from "@testing-library/dom";
import React, { useEffect, useState } from "react";
import "../css/Home.css";
import Button from 'react-bootstrap/Button'
import 'bootstrap/dist/css/bootstrap.min.css'
import Card from "react-bootstrap/Card";
import CardColumns from "react-bootstrap/CardColumns";

function Home() {
  const [images, setImages] = useState([
    {
      title: "",
      imagePath:"",
    },
  ]);
  useEffect(() => {
    fetch("/story")
      .then((res) => {
        if (res.ok) {
          return res.json();
        }
      })
      .then((jsonRes) => setImages(jsonRes));
  },[]);


  return (
    <div className="home-container">
      <div className="content-container">
        <div className="first-banner">
          <div className="first-title">
            <h1>포항연탄은행</h1>
            <h2>연탄은 사랑의 작은 실천입니다</h2>
            <h3>조그만 연탄 한 장이 누군가에게는 따뜻한 하루를 선물해줄 수 있습니다</h3>
            <Button className="first-button">자세히 보기</Button>
          </div>
        </div>
        <div className="second-banner">
          <div className="second-title">
            <h1>최근 소식</h1>
          </div>
        </div>
        <div className="third-banner">
        <div className="third-card-container">
        <CardColumns>
            {images.slice(0,0+3).map((image, index) => (
              <div key={index}>
                <Card style={{ width: "250px",margin:'5px'}}>
                  <Card.Img variant="top" src={image.imagePath} height="200px"  />
                  <Card.Body height="10px" style={{padding:"0px"}}>
                    <Card.Title style={{textAlign:"center",color:"red"}}>{image.title}</Card.Title>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </CardColumns>
        </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
